page.css:
#main-page {
    position: absolute;
    top: 50px;
    left: 0;
    width: 100%;
    height: calc(100vh - 50px)
}

#main-header {
    position: absolute;
    top: 10vh;
    left: 50%;
    transform: translate(-50%, 0);
    font-size: 50px;
    text-align: center;
    font-family: 'Lexend Mega', sans-serif;
    color: #fff
}

#main-description {
    position: absolute;
    top: 32vh;
    left: 50%;
    width: 60%;
    transform: translate(-50%, 0);
    font-size: 20px;
    text-align: center;
    color: #fff
}

#input {
    position: absolute;
    top: 60vh;
    left: 50%;
    background: rgba(0, 0, 0, .5);
    transform: translate(calc(-50% - 50px), 0);
    border-width: 3px;
    border-top-left-radius: 10px;
    border-bottom-left-radius: 10px;
    border-style: solid;
    padding-left: 20px;
    border-color: #fff;
    color: #fff;
    width: 232px;
    height: 60px;
    font-size: 15px
}

#input::placeholder {
    color: #bbb
}

#submit {
    position: absolute;
    top: 60vh;
    left: 50%;
    transform: translate(calc(-50% + 130px), 0);
    background: rgba(0, 0, 0, .5);
    border-width: 3px;
    border-top-right-radius: 10px;
    border-bottom-right-radius: 10px;
    border-style: solid;
    border-left-style: none;
    border-color: #fff;
    color: #fff;
    width: 100px;
    height: 68px;
    font-size: 15px
}
