page.css:
#main-description-container {
    position: absolute;
    top: 5%;
    left: 50%;
    width: 60%;
    height: calc(90% - 40px);
    background-color: #000000bb;
    transform: translate(-50%, 0);
    font-size: 15px;
    font-family: 'Lexend Mega', sans-serif;
    text-align: center;
    border-radius: 25px;
    border: 3px solid white;
    padding: 20px;
    overflow: scroll;
}
#main-description {
    font-size: 15px;
    color: #ffffff;
    font-family: 'Lexend Mega', sans-serif;
}
